(deftest regex-matching
  (testing "contains?? returns true if regex matches"
    (is (true? (contains?? #"hello" "Hi there hello"))))
  
  (testing "tweet-contains? return false if regex doesn't match"
    (is (false? (tweet-contains? #"trump" tweet)))))

